function abreOuFecha(e){e.target.parentNode.classList.toggle("faq__questions__item--is--open")}function removeAtivo(){var t=document.querySelectorAll("[data-tab-button]");for(let e=0;e<t.length;e++)t[e].classList.remove("temas__link__buttons--is--active")}function escondeAbas(){var t=document.querySelectorAll("[data-tab-id]");for(let e=0;e<t.length;e++)t[e].classList.remove("titulos__opcoes__link--is--active")}document.addEventListener("DOMContentLoaded",function(){var t=document.querySelectorAll("[data-tab-button]"),o=document.querySelectorAll("[data-faq-question]");const e=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",function(){window.scrollY<e?document.querySelector("header").classList.add("header--is--hidden"):document.querySelector("header").classList.remove("header--is--hidden")});for(let e=0;e<t.length;e++)t[e].addEventListener("click",function(e){var t=e.target.dataset.tabButton,t=document.querySelector(`[data-tab-id=${t}]`);escondeAbas(),t.classList.add("titulos__opcoes__link--is--active"),removeAtivo(),e.target.classList.add("temas__link__buttons--is--active")});for(let e=0;e<o.length;e++)o[e].addEventListener("click",abreOuFecha)});